import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import ChatBubble from "./chat-bubble";
import { useCallback, useEffect, useRef, useState } from "react";
import { ScrollArea } from "./ui/scroll-area";
import useStore, { findArticle, selector } from "./store";
import { shallow } from "zustand/shallow";

type Message = {
  content: string;
  fromBot: boolean;
};

export function ChatBox() {
  const [typedMessage, setTypedMessage] = useState("");
  const [messages, setMessages] = useState<Message[]>([]);
  const inputRef = useRef<HTMLInputElement>(null);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const { updateNodeType } = useStore(selector, shallow);

  useEffect(() => {
    setTimeout(
      () => messagesEndRef.current?.scrollIntoView({ behavior: "smooth" }),
      100
    );
  }, [messages]);

  const fetchBot = async (query: string) => {
    const res = await fetch("http://localhost:4000/chat", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ query }),
    });
    const data: {
      response: string;
      related_files: string[];
    } = await res.json();

    return data;
  };

  const send = useCallback(() => {
    if (typedMessage === "") return;

    setMessages((prev) => [...prev, { content: typedMessage, fromBot: false }]);
    // clear input
    setTypedMessage("");
    if (inputRef.current) {
      inputRef.current.value = "";
    }
    fetchBot(typedMessage).then((data) => {
      const articleNodeIds = data.related_files.map(
        (s) => s.match(/^dataset\/([a-Ñ0-9]+)\.txt$/)?.[1] ?? ""
      );

      updateNodeType(articleNodeIds, "article-node");
      setMessages((prev) => [
        ...prev,
        { content: data.response, fromBot: true },
      ]);
    });
  }, [typedMessage, updateNodeType]);

  useEffect(() => {
    const sendMsg = (e: KeyboardEvent) => {
      if (e.key === "Enter") {
        send();
      }
    };

    addEventListener("keydown", sendMsg);

    return () => {
      removeEventListener("keydown", sendMsg);
    };
  });

  return (
    <div className="w-100 h-full flex flex-col gap-2">
      <ScrollArea className="h-full">
        <div className="flex flex-col gap-2 pr-5">
          {messages.map((msg, i) => (
            <ChatBubble
              key={i}
              name={msg.fromBot ? "Bot" : "You"}
              message={msg.content}
              other={msg.fromBot}
            />
          ))}
          <div ref={messagesEndRef} />
        </div>
      </ScrollArea>
      <div className="flex gap-2">
        <Input
          ref={inputRef}
          onChange={(el) => {
            setTypedMessage(el.target.value);
          }}
        />
        <Button onClick={send}>Search</Button>
      </div>
    </div>
  );
}
